var mapping = BindingSource.Current as Sage.Entity.Interfaces.IBODMapping;
if (mapping != null)
{
    //Handle cloning option
    if (Request.QueryString["copybodmapping"] != null && Request.QueryString["entityId"] != null &&
        Request.QueryString["copybodmapping"].Equals("true"))
    {
        var copyFromBodMapping = Sage.Platform.EntityFactory.GetById<Sage.Entity.Interfaces.IBODMapping>(Request.QueryString["entityId"]);
        mapping.CloneBodMapping(copyFromBodMapping);
    }

    cboEntity.Items.Clear();
	cboEntity.Items.Add(new ListItem { Text = "", Value = "" });
    var ts = Sage.Platform.Application.ApplicationContext.Current.Services.Get<System.ComponentModel.Design.ITypeDiscoveryService>();
    var types = ts.GetTypes(typeof (Sage.Platform.Orm.Interfaces.IPersistentEntity), true);
    foreach (Type entity in types)
    {
        string name = entity.Name;
        if (name.StartsWith("I"))
        {
            name = name.Remove(0, 1);
        }
        cboEntity.Items.Add(new ListItem {Text = name, Value = name, Selected = mapping.EntityName == name});
        if (mapping.EntityName == name)
        {
            //load and set the Inherited Owner From
            cboParentEntity.Items.Clear();
			cboParentEntity.Items.Add(new ListItem { Text = "<None>", Value = ""});
            var properties = entity.GetProperties();
            foreach (var property in properties)
            {
                if (property.PropertyType.IsInterface)
                {
                    cboParentEntity.Items.Add(new ListItem {Text = property.Name, Value = property.Name, Selected = mapping.RecordOwner == property.Name});
                }
            }
			if(mapping.EntityName == "Account")
				cboParentEntity.Items.Add(new ListItem { Text = "Account", Value = "ParentId#Account" ,Selected = mapping.RecordOwner == "ParentId#Account"  });
        }
    }
    cboEntity.Items.Add(new ListItem {Text = "PickList", Value = "PickList", Selected = mapping.EntityName == "PickList"});
	if(mapping.EntityName == "PickList")
	{
		cboParentEntity.Items.Clear();
		cboParentEntity.Items.Add(new ListItem { Text = "<None>", Value = "" ,Selected = true });
	}
}

cboParentEntity.Items.Clear();
cboParentEntity.Items.Add(new ListItem {Text = "<None>", Value = ""});
var ts = Sage.Platform.Application.ApplicationContext.Current.Services.Get<System.ComponentModel.Design.ITypeDiscoveryService>();
var types = ts.GetTypes(typeof(Sage.Platform.Orm.Interfaces.IPersistentEntity), true);
var entityName = "Sage.Entity.Interfaces.I" + cboEntity.SelectedValue + ",Sage.Entity.Interfaces";
foreach (System.ComponentModel.PropertyDescriptor property in System.ComponentModel.TypeDescriptor.GetProperties(Type.GetType(entityName)))
{
    if (property.PropertyType.IsInterface)
    {
        cboParentEntity.Items.Add(new ListItem { Text = property.Name, Value = property.Name });
    }	
}
if(cboEntity.SelectedValue == "Account")
	cboParentEntity.Items.Add(new ListItem { Text = "Account", Value = "ParentId#Account" });
